import React from 'react';

const Nav = () => {
    const [showSubMenu, setShowSubMenu] = React.useState(false);

    const handleMouseEnter = () => {
      setShowSubMenu(true);
    };
  
    const handleMouseLeave = () => {
      setShowSubMenu(false);
    };
  return (
        <>
          <Navbar bg="primary" variant="dark" fixed="top">
            <Navbar.Brand>Unidad Familia Medellin</Navbar.Brand>
            <Navbar.Toggle aria-controls="navbar-nav" />
            <Navbar.Collapse id="navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href="/">Inicio</Nav.Link>
                <NavDropdown
                  title="¿Quienes somos?"
                  id="quienes-somos-dropdown"
                  show={showSubMenu}
                  onMouseEnter={handleMouseEnter}
                  onMouseLeave={handleMouseLeave}
                >
                  <NavDropdown.Item href="#">Contactenos</NavDropdown.Item>
                  <NavDropdown.Item href="#">Unidad Familia</NavDropdown.Item>
                </NavDropdown>
                <NavDropdown title="Noticias" id="noticias-dropdown">
                  <NavDropdown.Item href="#">Contactenos</NavDropdown.Item>
                  <NavDropdown.Item href="#">SIOF</NavDropdown.Item>
                </NavDropdown>
                <Nav.Link href="#">Multimedia</Nav.Link>
              </Nav>
              <Nav>
                <Nav.Link className="me-2" style={{ border: '1px solid #00B0F6', color: '#00B0F6' }}>
                  Login UFM
                </Nav.Link>
                <Nav.Link style={{ border: '1px solid #00B0F6', background: '#00B0F6' }}>Login Admin</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          {/* Resto del contenido de la aplicación */}
        </>
      );
    };
    

export {Nav};
