import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import './nav.css'

import React, { useState } from 'react';

const Nava = () => {
    const [showSubMenu, setShowSubMenu] = useState(false);

    const showSubMenuHandler = () => {
      setShowSubMenu(true);
    };
  
    const hideSubMenuHandler = () => {
      setShowSubMenu(false);
    };
  
    return (
      <div>
        <div
          style={{
            backgroundColor: '#00B0F6',
            width: '100%',
            height: '40px',
            fontSize: '25px',
            color: '#ffffff',
            position: 'fixed', // Agregado: Menú fijo en la parte superior
            top: 0, // Agregado: Posición superior 0
            left: 0, // Agregado: Posición izquierda 0
          }}
          className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom"
        >
          Unidad Familia Medellin
        </div>
  
        <div
          style={{
            backgroundColor: '#ffffff',
            opacity: 1,
            marginTop: '79px !important', // Actualizado: Incremento del margen superior para evitar superposición
            fontSize: '18px',
          }}
          className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom"
        >
          {/* Resto del código del menú */}
        </div>
      </div>
    );
  };
  
export {Nava};
