import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import './nav.css'

import { Col, Container, Row } from "react-bootstrap";
import React, { useState } from 'react';

const Nava = () => {
    const [showSubMenu, setShowSubMenu] = useState(false);

    const handleMouseEnter = (event) => {
      const submenu = event.currentTarget.querySelector('.submenu');
      if (submenu) {
        submenu.classList.add('show');
      }
      setShowSubMenu(true);
    };
  
    const handleMouseLeave = (event) => {
      const submenu = event.currentTarget.querySelector('.submenu');
      if (submenu) {
        submenu.classList.remove('show');
      }
      setShowSubMenu(false);
    };
  return (
    <>
      <div
        style={{
          backgroundColor: '#00B0F6',
          width: '100%',
          height: '40px',
          fontSize: '25px',
          color: '#ffffff',
        }}
        className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom fixed-top"
      >
        Unidad Familia Medellin
      </div>

      <div
        className=" primer d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom fixed-top"
      >
        <div className="col-md-3 mb-2 mb-md-0">
          <img
            style={{ paddingLeft: '30px', paddingBottom: '100px' }}
            width="150"
            height="100"
            role="img"
            src="https://cdnwordpresstest-f0ekdgevcngegudb.z01.azurefd.net/es/wp-content/uploads/2022/03/Open-Graph-.png"
            alt=""
          />
          <a href="/" className="d-inline-flex link-body-emphasis text-decoration-none"></a>
        </div>

        <nav className="nav col-md-4 mb-2 justify-content-center mb-md-0">
          <ul className="nav">
            <li>
              <div
                className="menu-item"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <a href="./" className="nav-link px-2">
                  Inicio
                </a>
              </div>
            </li>
            <li>
              <div
                className="menu-item"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <a href="#" className="nav-link px-2">
                  Â¿Quienes somos?
                </a>
               
              </div>
            </li>
            <li>
              <div
                className="menu-item"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <a href="#" className="nav-link px-2">
                  Noticias
                </a>
              
              </div>
            </li>
            <li>
              <div
                className="menu-item"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <a href="#" className="nav-link px-2">
                  Multimedia
                </a>
              </div>
            </li>
          </ul>
        </nav>

        <div className="col-md-3 text-end">
          <button
            type="button"
            style={{ border: '1px solid #00B0F6 !important', color: '#00B0F6 !important' }}
            className="btn me-2 login"
          >
            Login UFM
          </button>
          <button
            type="button"
            style={{ border: '1px solid #00B0F6 !important', background: '#00B0F6 !important' }}
            className="btn btn-primary"
          >
            Login Admin
          </button>
        </div>
      </div>
    </>
  );
};

export {Nava};
