import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import './nav.css'

import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';

const Nava = () => {
    const [showSubMenu1, setShowSubMenu1] = useState(false);
    const [showSubMenu2, setShowSubMenu2] = useState(false);
  
    const showSubMenuHandler1 = () => {
      setShowSubMenu1(true);
    };
  
    const hideSubMenuHandler1 = () => {
      setShowSubMenu1(false);
    };
  
    const showSubMenuHandler2 = () => {
      setShowSubMenu2(true);
    };
  
    const hideSubMenuHandler2 = () => {
      setShowSubMenu2(false);
    };
  
    return (
      <div>
        <Navbar bg="light" expand="md" fixed="top">
          <Navbar.Brand href="/">
            <img
              src="https://cdnwordpresstest-f0ekdgevcngegudb.z01.azurefd.net/es/wp-content/uploads/2022/03/Open-Graph-.png"
              alt=""
              width="150"
              height="100"
            />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="/">Inicio</Nav.Link>
              <NavDropdown
                title="¿Quienes somos?"
                id="collasible-nav-dropdown"
                show={showSubMenu1}
                onMouseEnter={showSubMenuHandler1}
                onMouseLeave={hideSubMenuHandler1}
              >
                <NavDropdown.Item href="/contactenos">
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Contactenos&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                </NavDropdown.Item>
                <NavDropdown.Item href="/ufm">
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Unidad Familia&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                </NavDropdown.Item>
              </NavDropdown>
              <NavDropdown
                title="Noticias"
                id="collasible-nav-dropdown"
                show={showSubMenu2}
                onMouseEnter={showSubMenuHandler2}
                onMouseLeave={hideSubMenuHandler2}
              >
                <NavDropdown.Item href="#">
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Contactenos&nbsp;&nbsp;&nbsp;&nbsp;
                </NavDropdown.Item>
                <NavDropdown.Item href="#">
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SIOF&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                </NavDropdown.Item>
              </NavDropdown>
              <Nav.Link href="#">Multimedia</Nav.Link>
            </Nav>
            <Nav>
              <Nav.Link href="#" style={{ border: '1px solid #00B0F6', color: '#00B0F6' }}>
                Login UFM
              </Nav.Link>
              <Nav.Link href="#" style={{ border: '1px solid #00B0F6', background: '#00B0F6' }}>
                Login Admin
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
  
        <div
          style={{
            backgroundColor: '#00B0F6',
            width: '100%',
            height: '40px',
            fontSize: '25px',
            color: '#ffffff',
          }}
          className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom fixed-top"
        >
          Unidad Familia Medellin
        </div>
  
        <div
          style={{
            backgroundColor: '#ffffff',
            opacity: 1,
            marginTop: '79px !important',
            fontSize: '18px',
          }}
          className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between border-bottom fixed-top"
        >
          {/* Resto del código */}
        </div>
      </div>
    );
  };
  

export {Nava};
